    public static String getAppPrivateKey(String appId){
        if (null == appId || appId.isEmpty()){
            return null;
        }
        if ("11".equals(appId)){
            return APIID_11_APP_PRIVATE_KEY_VALUE;
        }else if ("15".equals(appId)){
            return APIID_11_APP_PRIVATE_KEY_VALUE;
        }
        else{
            return FC_APP_PRIVATE_KEY_VALUE;
        }
    }

    public static String getAliPayPublicKey(String appId){
        if (null == appId || appId.isEmpty()){
            return null;
        }
        if ("11".equals(appId)){
            return APIID_11_ALIPAY_PUBLIC_KEY_VALUE;
        }else if ("15".equals(appId)){
            return APIID_11_ALIPAY_PUBLIC_KEY_VALUE;
        }else{
            return ALIPAY_PUBLIC_KEY_VALUE;
        }
    }

